mapper.insert:

	SELECT NVL(MAX(employee_id),0) + 1
	FROM employees

INSERT INTO employees (employee_id
                       , last_name
                       , email
                       , hire_date

                       , job_id


                       , salary
                       )
VALUES (#{employeeId}
        , #{lastName}
        , #{email}


        	, #{hireDate}


        	, sysdate



        , #{jobId}


        , #{salary}

        )

mapper.update:
UPDATE employees
SET last_name = #{lastName}

    , email = #{email}


    , hire_date = #{hireDate} 


    , salary = #{salary}

WHERE employee_id = #{employeeId}
